
package exercises;

public class Cons {

	public Cons() { // Constructor name same as class name
		System.out.println("Constructor is created.");
	} // Does not have a return type

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Cons c1 = new Cons();
		System.out.println("Thus, a constructor is invoked whenever an object is created.");
	}

}
